CREATE TABLE IF NOT EXISTS `clients` (
	`id` int AUTO_INCREMENT NOT NULL,
	`name` varchar(255) NOT NULL,
	`cnpj` varchar(18),
	`email` varchar(320),
	`phone` varchar(20),
	`address` text,
	`notes` text,
	`active` boolean NOT NULL DEFAULT true,
	`createdBy` int NOT NULL,
	`createdAt` timestamp NOT NULL DEFAULT (now()),
	`updatedAt` timestamp NOT NULL DEFAULT (now()) ON UPDATE CURRENT_TIMESTAMP,
	CONSTRAINT `clients_id` PRIMARY KEY(`id`)
);

ALTER TABLE `users` MODIFY COLUMN `role` enum('diretor','comprador','almoxarife','manutencao','financeiro') NOT NULL DEFAULT 'comprador';

ALTER TABLE `budget_items` ADD COLUMN IF NOT EXISTS `unitPrice` decimal(12,2) NOT NULL DEFAULT 0;
ALTER TABLE `budget_items` ADD COLUMN IF NOT EXISTS `totalPrice` decimal(12,2) NOT NULL DEFAULT 0;
ALTER TABLE `budgets` ADD COLUMN IF NOT EXISTS `clientId` int NOT NULL DEFAULT 1;
ALTER TABLE `budgets` ADD COLUMN IF NOT EXISTS `validUntil` varchar(10);
ALTER TABLE `budgets` ADD COLUMN IF NOT EXISTS `observations` text;

